import logo from './logo.svg';
import './App.css';
import React from 'react'
import Chronometer from './Chronometer';

class App extends React.Component {
  constructor(){
    super();
    this.state = {
      showChronometer:false,
    }
  }

  changeChronometer = () => {
    this.setState((state) => ({showChronometer : !state.showChronometer}));
  }
  render () {
  const { showChronometer } = this.state;
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        {showChronometer && <Chronometer/>}
        <button onClick={this.changeChronometer}>Play</button>
      </header>
    </div>
  );
  }
}
export default App;
