import React from 'react'
import './Chronometer.css'

class Chronometer extends React.Component {
  constructor(){
    super();
    this.state = {
      seconds: '59',
      minutes: '02',
      hour: '00',
    }
  }

  componentDidMount() {
      this.interval = setInterval(() => {
      console.log('aqui')
      this.setState((state) => ({
        seconds: state.seconds.legth === 2 ? (parseInt(state.seconds) -1).toString() : console.log(state.seconds.legth),
      }));
    }, 100);
  }

  componentDidUpdate(prevProps, prevState) {
    if( prevState.seconds === '0' && prevState.minutes === '0' && prevState.hour === '00'){
      this.setState({
        seconds: '00',
        minutes: '00',
        hour: '00',
    });
      clearInterval(this.interval);
    }
    else if( prevState.seconds === '0' && prevState.minutes === '0'){
      this.setState((state) => ({
        seconds: '59',
        minutes: '59',
        hour: (parseInt(state.minutes) -1).toString(),
    }));
    }
    else if( prevState.seconds === '0' ){
      this.setState((state) => ({
        seconds: '59',
        minutes: (parseInt(state.minutes) -1).toString(),
        hour: '00',
    }));
    }
  }

  render(){
    const { seconds, minutes, hour} = this.state

  return (
    <div className="Chronometer">
      <p>{`${hour}:${minutes}:${seconds}`}</p>      
    </div>
  );
}
}

export default Chronometer;
