import logo from './logo.svg';
import './App.css';
import React from 'react'
import Chronometer from './Chronometer';

class App extends React.Component {
  constructor(){
    super();
    this.state = {
      showChronometer:false,
      valorDefinido: '',
    }
  }

  changeValue = () => {
    this.setState({
      valorDefinido: '05',
    });
  }

  changeChronometer = () => {
    this.setState((state) => ({showChronometer : !state.showChronometer}));
  }
  render () {
  const { valorDefinido } = this.state;
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        {showChronometer ? `${'00'}:${valorDefinido}:${'00'}` : <Chronometer valorDefinido={ valorDefinido }/>}
        <button onClick={this.changeChronometer}>Play</button>
        <button onClick={this.changeValue}>05:00</button>
      </header>
    </div>
  );
  }
}
export default App;
