import './App.css';
import React from 'react'
import Chronometer from './Chronometer';

class App extends React.Component {
  constructor(){
    super();
    this.state = {
      showImage:false,
    }
  }

  changeImage = () => {
    this.setState = {
      showImage: !this.state.showImage,
    }
  }

  render () {
    const { showImage } = this.state;
  return (
    <div className="App">
      <header className="App-header">
        {showImage ? '' : <img onClick={this.changeImage} src='/logoTrybe.svg' className="App-logo" alt="logo" />}
        <Chronometer className="Chronometer"/>
      </header>
    </div>
  );
  }
}
export default App;
