import React from 'react'
import './Chronometer.css'

class Chronometer extends React.Component {
  constructor(){
    super();

    this.state = {
      seconds: '00',
      minutes: '00',
      hour: '00',
      update: false,
    }
  }

  convertString(element){
    return (parseInt(element)).toString();
  }

  changeState = ({ target }) => {
    const { name } = target;
    const value = !target.value ? '00': target.value;
    this.setState({
      [name]: this.convertString(value).length === 2 ? this.convertString(value) : (`0${this.convertString(value)}`),
      update: false,
  });
  }

  convertSubtraiString(element){
    return (parseInt(element)-1).toString();
  }

  stopChronometer = () => {
    clearInterval(this.interval);
  }

  clearChronometer = () => {
    this.setState({
      seconds: '00',
      minutes: '00',
      hour: '00',
  });
  }

  changeChronometer = () => {
    this.interval = setInterval(() => {
    console.log('aqui')
    this.setState((state) => ({
      seconds: parseInt(state.seconds) == 0 ? '59' : (this.convertSubtraiString(state.seconds).length === 2 ? this.convertSubtraiString(state.seconds) : (`0${this.convertSubtraiString(state.seconds)}`)),
      minutes: parseInt(state.seconds) == 0 ? (this.convertSubtraiString(state.minutes).length === 2 ? this.convertSubtraiString(state.minutes) : (`0${this.convertSubtraiString(state.minutes)}`)) : state.minutes,
      update: true,
    }));
  }, 1000);
  }

  componentDidUpdate(prevProps, prevState) {
    if(prevState.update){
      if( prevState.seconds === '00' && prevState.minutes === '00' && prevState.hour === '00'){
        this.setState({
          seconds: '00',
          minutes: '00',
          hour: '00',
      });
        clearInterval(this.interval);
      }
      else if( prevState.seconds === '00' && prevState.minutes === '00'){
        this.setState((state) => ({
          seconds: '59',
          minutes: '59',
          hour: (parseInt(state.minutes) -1).toString(),
      }));
      }
      else if( prevState.seconds === '00'){
        console.log('olha')
        this.setState((state) => ({
          seconds: '59',
          minutes: this.convertSubtraiString(state.minutes).length === 2 ? this.convertSubtraiString(state.minutes) : (`0${this.convertSubtraiString(state.minutes)}`),
          hour: '00',
      }));
      }
    }
  }

  render(){
    const { seconds, minutes, hour} = this.state

  return (
    <div>
      <p className="Chronometer">{`${hour}:${minutes}:${seconds}`}</p>  
      <button className="buttonChronometer" onClick={this.changeChronometer}>Play</button>
      <button className="buttonChronometer" onClick={this.stopChronometer}>Stop</button>
      <button className="buttonChronometer" onClick={this.clearChronometer}>Clear</button>
      <form>
        <input className="inputChronometer" name='minutes' type='button' onClick={this.changeState} value='5:00'/>
        <input className="inputChronometer" name='minutes' type='button' onClick={this.changeState} value='10:00'/>
        <input className="inputChronometer" name='minutes' type='button' onClick={this.changeState} value='15:00'/>
      </form>
      <form>
        <input className="textChronometer" name='hour' type='number' onChange={this.changeState} min="0" max="24" placeholder="hour" required/> 
        <input className="textChronometer" name='minutes' type='number' onChange={this.changeState} min="0" max="59" placeholder="minutes" required/> 
        <input className="textChronometer" name='seconds' type='number' onChange={this.changeState} min="0" max="59" placeholder="seconds" required/> 
       </form>
    </div>
  );
}
}

export default Chronometer;
