import React from 'react'
import './Chronometer.css'

class Chronometer extends React.Component {
  constructor(){
    super();
    this.state = {
      seconds: '50',
      minutes: '00',
      hour: '00',
      showChronometer:false,
    }
  }

  changeChronometer = () => {
    this.setState((state) => ({showChronometer : !state.showChronometer}));
  }





  render(){
    const { seconds, minutes, hour, showChronometer } = this.state

    if(seconds === '00'){
      clearInterval()
    }

    if(showChronometer === "Play"){
      setInterval(() => {
        console.log('aqui')
        this.setState((state) => ({
          seconds: (parseInt(state.seconds) -1).toString(),
        }));
      }, 1000);
    }

  return (
    <div className="Chronometer">
      <p>{`${hour}:${minutes}:${seconds}`}</p>      
      <button onClick={this.changeChronometer}>
        {showChronometer ? "Stop" : "Play"}
      </button>
    </div>
  );
}
}

export default Chronometer;
