import React from 'react'
import './Chronometer.css'

class Chronometer extends React.Component {
  constructor(){
    super();

    this.state = {
      seconds: '59',
      minutes: '03',
      hour: '00',
    }
  }

  convertSubtraiString(element){
    return (parseInt(element)).toString();
  }

  stopChronometer = () => {
    clearInterval(this.interval);
  }

  clearChronometer = () => {
    this.setState({
      seconds: '00',
      minutes: '00',
      hour: '00',
  });
  }

  changeState = ({ target }) => {
    const { name } = target
    const value = target.value === NaN ? '00' : target.value;
    console.log(value);
    this.setState({
      [name]: this.convertSubtraiString(value).length === 2 ? this.convertSubtraiString(value) : (`0${this.convertSubtraiString(value)}`),
  });
  }

  changeChronometer = () => {
      this.interval = setInterval(() => {
      console.log('aqui')
      this.setState((state) => ({
        seconds: this.convertSubtraiString(state.seconds).length === 2 ? this.convertSubtraiString(state.seconds) : (`0${this.convertSubtraiString(state.seconds)}`),
      }));
    }, 1000);
  }

  componentDidUpdate(prevProps, prevState) {
    if( prevState.seconds === '00' && prevState.minutes === '00' && prevState.hour === '00'){
      this.setState({
        seconds: '00',
        minutes: '00',
        hour: '00',
    });
      clearInterval(this.interval);
    }
    else if( prevState.seconds === '00' && prevState.minutes === '00'){
      this.setState((state) => ({
        seconds: '59',
        minutes: '59',
        hour: (parseInt(state.minutes) -1).toString(),
    }));
    }
    else if( prevState.seconds === '00' ){
      this.setState((state) => ({
        seconds: '59',
        minutes: this.convertSubtraiString(state.minutes).length === 2 ? this.convertSubtraiString(state.minutes) : (`0${this.convertSubtraiString(state.minutes)}`),
        hour: '00',
    }));
    }
  }

  render(){
    const { seconds, minutes, hour} = this.state

  return (
    <div className="Chronometer">
      <p>{`${hour}:${minutes}:${seconds}`}</p>  
      <button onClick={this.changeChronometer}>Play</button>
      <button onClick={this.stopChronometer}>Stop</button>
      <button onClick={this.clearChronometer}>Clear</button>
      <form>
        <input name='hour' type='number' onChange={this.changeState} /> 
        <input name='minutes' type='number' onChange={this.changeState}/> 
        <input name='seconds' type='number' onChange={this.changeState}/> 
      </form>
    </div>
  );
}
}

export default Chronometer;
